class PagesController < ApplicationController
  def home
    @sports = Sport.all
    if current_user
      @myroutines = Routine.where(imageable_type: 'User').where(imageable_id: current_user.id)
    else
      redirect_to new_user_session_path
    end
    a = "data:image/png;base64,"
    b = "data:image/jpeg;base64,"
    c = "data:image/jpeg;base64,"
    d = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQn95SF0A3kdCSrWuljZ2aM7d_M7tgz780UDA&usqp=CAU"
    e = "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS9th9RUZYWe3bfe3PE8wjMVajiZ28A5bsUzQ&usqp=CAU"
    @photos = [a, b, c, d, e]
  end
end
